const throttle1 = (fn, wait) => {
  let last = 0
  return function () {
    let now = Date.now()
    if (now - last >= wait) {
      last = now
      fn.apply(this, arguments)
    }
  }
}

// å®šæ—¶å™¨å†™æ³•ï¼ˆç¬¬ä¸€æ¬¡ä¹Ÿè¦å»¶è¿Ÿæ‰§è¡Œï¼‰
const throttle2 = (fn, wait) => {
  let timer = null
  return function () {
    if (!timer) {
      timer = setTimeout(() => {
        fn.apply(this, arguments)
        timer = null
      }, wait)
    }
  }
}

// ç»“åˆå†™æ³•ï¼ˆç¬¬ä¸€æ¬¡è¦å»¶è¿Ÿæ‰§è¡Œï¼Œæœ€åä¸€æ¬¡ä¸å»¶è¿Ÿè¦ç«‹å³æ‰§è¡Œï¼‰
const throttle = (func, wait, options) => {
  var context, args, result
  var timeout = null
  var previous = 0
  if (!options) options = {}
  var later = function() {
    previous = options.leading === false ? 0 : Date.now()
    timeout = null
    result = func.apply(context, args)
    if (!timeout) context = args = null
  }
  return function() {
    var now = Date.now()
    if (!previous && options.leading === false) previous = now
    // è®¡ç®—å‰©ä½™æ—¶é—´
    var remaining = wait - (now - previous)
    context = this
    args = arguments
    // å½“åˆ°è¾¾waitæŒ‡å®šçš„æ—¶é—´é—´éš”ï¼Œåˆ™è°ƒç”¨funcå‡½æ•°
    // ç²¾å½©ä¹‹å¤„ï¼šæŒ‰ç†æ¥è¯´remaining  wait) {
      // ç”±äºsetTimeoutå­˜åœ¨æœ€å°æ—¶é—´ç²¾åº¦é—®é¢˜ï¼Œå› æ­¤ä¼šå­˜åœ¨åˆ°è¾¾waitçš„æ—¶é—´é—´éš”ï¼Œä½†ä¹‹å‰è®¾ç½®çš„setTimeoutæ“ä½œè¿˜æ²¡è¢«æ‰§è¡Œï¼Œå› æ­¤ä¸ºä¿é™©èµ·è§ï¼Œè¿™é‡Œå…ˆæ¸…ç†setTimeoutæ“ä½œ
      if (timeout) {
        clearTimeout(timeout)
        timeout = null
      }
      previous = now
      result = func.apply(context, args)
      if (!timeout) context = args = null
    } else if (!timeout && options.trailing !== false) {
      // options.trailing=trueæ—¶ï¼Œå»¶æ—¶æ‰§è¡Œfuncå‡½æ•°
      timeout = setTimeout(later, remaining)
    }
    return result
  }
}

