import { VueConstructor } from 'vue/types'

interface RouteConfig {
  path: string,
  name: string,
  component: VueConstructor
}

type Routes = Array

class Hash {
  listen(cb: Function) {
    window.addEventListener('hashchange', path => {
      cb && cb(window.location.hash)
    })
  }
}

export default class VueRouter {
  static install: (Vue: any) => void
  routes: Routes
  hash: any
  vm: any
  path: string
  constructor({ routes }: any) {
    this.routes = routes
    this.hash = new Hash()
    this.path = '/'
    this.hash.listen((path: string) => {
      this.path = path.slice(1)
      this.vm.$forceUpdate()
    })
  }

  init(vm: any) {
    this.vm = vm
  }
}

VueRouter.install = function(Vue: any) {
  Vue.mixin({
    beforeCreate() {
      if (this.$options.router) {
        this.$options.router.init(this)
        Vue.util.defineReactive(this, '_route', this.$options.router)
      }
    }
  })
  Vue.component('router-view', {
    functional: true,
    render(createElement: any, { props, children, parent, data }: any) {
      const router =  parent.$parent._route
      const routes = router.routes
      const path = router.path
      let comp
      routes.forEach((item: any)=> {
        Object.keys(item).forEach((key: any) => {
          if (item[key] === path) {
            comp = item.component
          }
        })
      })
      return createElement(comp)
    }
  })
}

hash模式实现关键点：

hashchange 监听需要 this.vm.$forceUpdate() 强制更新
Vue.util.defineReactive(this, '_route', this.$options.router) 将 router 进行双向绑定

